definition.script:
// Command-line options (taken from https://jenkins.io/doc/pipeline/examples/):
// --batch-mode : recommended in CI to inform maven to not run in interactive mode (less logs)
// -V : strongly recommended in CI, will display the JDK and Maven versions in use.
//      Very useful to be quickly sure the selected versions were the ones you think.
// -U : force maven to update snapshots each time (default : once an hour, makes no sense in CI).
// -Dsurefire.useFile=false : useful in CI. Displays test errors in the logs directly (instead of
//                            having to crawl the workspace files to see the cause).
// -e : Display stack-traces on failure
// -------------------------------------------------------------------------------------------------
//
//
// GCC versions
// ------------
//
// GCC outputs dynamically-linked libraries that depend on specific versions of the glibc library.
// https://gcc.gnu.org/onlinedocs/libstdc++/manual/abi.html lists which GLIBCXX version ships with each version of GCC.
// As of 2017-03-09, AWS linux images ship with GCC 4.8.3: https://aws.amazon.com/amazon-linux-ami/2015.09-release-notes/
// Therefore, we must use this version of GCC or older.
//
// Using a newer version of GCC results in the following error:
//
// java.lang.UnsatisfiedLinkError: librequirements.so: /usr/lib64/libstdc++.so.6: version `GLIBCXX_3.4.21' not found (required by librequirements.so)
//
// See http://stackoverflow.com/a/17275650/14731 for overriding the GCC version used by CMake


// Create release candidate branch
def repository = 'bitbucket.org/cowwoc/requirements/'
// One can look up a project's profileId by running: mvn nexus-staging:rc-list-profiles -DserverId=sonatype-nexus-staging -DnexusUrl=https://oss.sonatype.org/
def stagingProfileId = '353562598afa'
def stagingRepositoryId
def branch = 'default'

node("linux && amd64")
{
    // http://stackoverflow.com/a/33630506/14731
    withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'bitbucket', usernameVariable: 'HG_USERNAME', passwordVariable: 'HG_PASSWORD']])
    {
        def authenticatedRepository = "https://${HG_USERNAME}:${HG_PASSWORD}@${repository}"
        timeout(2)
        {
            stage("Checkout (linux-amd64)")
            {
                // Wipe out local repository in case it contains extra commits from a previous (broken) build
                deleteDir()
                checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "${branch}"]
            }
        }
        timeout(10)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin",
                "HGUSER=Gili Tzabari <gili.tzabari@gmail.com>"])
            {
                stage("Create release branch")
                {
                    // Check if tag exists: http://stackoverflow.com/a/41123272/14731
                    def tagExists = !sh(returnStatus: true, script: "hg id -q -r release-${releaseVersion} > /dev/null 2>&1").asBoolean()
                    if (tagExists)
                        echo 'Tag release-' + releaseVersion + ' already exists'
                    else
                    {
                        // Set commit username/email: http://stackoverflow.com/a/1401821/14731
                        // Commit without failure if nothing changed: http://stackoverflow.com/a/36117152/14731
                        // sh: check if string contains a value: http://stackoverflow.com/a/8811800/14731
                        echo 'Tagging release as release-' + releaseVersion
                        sh script: "mvn --batch-mode -V -e versions:set -DnewVersion=${releaseVersion} -DgenerateBackupPoms=false"
                        def hgSummary = sh(returnStdout: true, script: 'hg summary')
                        if (!hgSummary.contains("commit: (clean)"))
                            sh script: "hg commit -m \"[jenkins] Releasing version ${releaseVersion}\""

                        sh script: """hg tag release-${releaseVersion}
                            hg update ${branch}
                            mvn --batch-mode -V -e versions:set -DnewVersion=${nextVersion} -DgenerateBackupPoms=false
                            hg commit -m "[jenkins] Incrementing version to ${nextVersion}"
                            hg push ${authenticatedRepository}"""
                    }

                    // Open a new staging repository and capture its ID
                    def output = sh returnStdout: true, script: """hg update release-${releaseVersion}
                        mvn --batch-mode -U -e nexus-staging:rc-open -DstagingProfileId=${stagingProfileId}"""
                    echo output
                    stagingRepositoryId = ( output =~ /Opened (orgbitbucketcowwoc-\d+)/ ) [0][1]
                }
            }
        }
    }
}

// Build and release from all platforms
try
{
    parallel linux_i386:
    {
        // Linux-i386 deploys the portable modules as well as its platform-specific binaries.
        // Remaining platforms only deploy platform-specific binaries.
        node("linux && i386")
        {
            timeout(2)
            {
                stage("Checkout (linux-i386)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "release-${releaseVersion}", revisionType: 'TAG']
                }
            }
            timeout(15)
            {
                withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin"])
                {
                    // "portable" profile can't run unit tests so we skip the build step and deploy directly.
                    stage("Deploy (portable)")
                    {
                        // Deploy empty main JAR
                        sh script: "mvn --batch-mode -e -DosDetection=false -Pportable -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
                        archiveArtifacts: '**/target/*.jar'
                    }
                    stage("Build (linux-i386)")
                    {
                        sh script: '''sudo apt-get install cmake gcc-4.7 g++-4.7 -y
                            mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Plinux-x86_32-release clean install'''
                    }
                    stage("Deploy (linux-i386)")
                    {
                        // Deploy platform-specific JAR
                        // Use the same GCC version as AWS. See top of this script for more information.
                        sh script: """export CC=/usr/bin/gcc-4.7
                            export CXX=/usr/bin/g++-4.7
                            cd native
                            mvn --batch-mode -e -DosDetection=false -Plinux-x86_32-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts: '**/target/*.jar'
                    }
                }
            }
        }
    }, linux_amd64:
    {
        node("linux && amd64")
        {
            timeout(2)
            {
                stage("Checkout (linux-amd64)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "release-${releaseVersion}", revisionType: 'TAG']
                }
            }
            timeout(15)
            {
                withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin"])
                {
                    stage("Build (linux-amd64)")
                    {
                        sh script: '''sudo apt-get install cmake gcc-4.7 g++-4.7 -y
                            mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Plinux-x86_64-release clean install'''
                    }
                    stage("Deploy (linux-amd64)")
                    {
                        // Use the same GCC version as AWS. See top of this script for more information.
                        sh script: """export CC=/usr/bin/gcc-4.7
                            export CXX=/usr/bin/g++-4.7
                            cd native
                            mvn --batch-mode -e -DosDetection=false -Plinux-x86_64-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts: '**/target/*.jar'
                    }
                }
            }
        }
    }, mac:
    {
        node("mac")
        {
            timeout(2)
            {
                stage("Checkout (mac)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "release-${releaseVersion}", revisionType: 'TAG']
                }
            }
            timeout(15)
            {
                withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin:/usr/local/bin"])
                {
                    stage("Build (mac-x86_64)")
                    {
                        sh script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Pmac-x86_64-release clean install'
                    }
                    stage("Deploy (mac-x86_64)")
                    {
                        sh script: """cd native
                            mvn --batch-mode -e -DosDetection=false -Pmac-x86_64-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts: '**/target/*.jar'
                    }
                }
            }
        }
    }, windows:
    {
        node("windows && i386 && amd64")
        {
            timeout(2)
            {
                stage("Checkout (windows)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "release-${releaseVersion}", revisionType: 'TAG']
                }
            }
            timeout(15)
            {
                withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin;${tool 'mercurial'};${env.JAVA_HOME}/bin"])
                {
                    stage("Build (windows-x86)")
                    {
                        bat script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Pwindows-x86_32-release clean install'
                    }
                    stage("Deploy (windows-x86)")
                    {
                        bat script: """cd native
                            mvn --batch-mode -e -DosDetection=false -Pwindows-x86_32-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts: '**/target/*.jar'
                    }
                }
            }
            timeout(15)
            {
                withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin;${tool 'mercurial'};${env.JAVA_HOME}/bin"])
                {
                    stage("Build (windows-amd64)")
                    {
                        bat script: 'mvn --batch-mode -U -e -Dsurefire.useFile=false -DosDetection=false -Pwindows-x86_64-release clean install'
                    }
                    stage("Deploy (windows-amd64)")
                    {
                        bat script: """cd native
                            mvn --batch-mode -e -DosDetection=false -Pwindows-x86_64-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts: '**/target/*.jar'
                    }
                }
            }
        }
    },
    failFast: true
}
catch (e)
{
    echo "Dropping staging repository due to error"
    try
    {
        // Drop staging repository
        node("linux && amd64")
        {
            timeout(2)
            {
                stage("Checkout (linux-amd64)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "release-${releaseVersion}", revisionType: 'TAG']
                }
            }
            timeout(5)
            {
                withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin"])
                {
                    sh script: "mvn --batch-mode -U -e nexus-staging:rc-drop -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
                }
            }
        }
    }
    catch (e2)
    {
        // Ignore and throw the original exception
    }
    throw e;
}

// Close the release
node("linux && amd64")
{
    timeout(2)
    {
        stage("Checkout (linux-amd64)")
        {
            // Wipe out local repository in case it contains extra commits from a previous (broken) build
            deleteDir()
            checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "release-${releaseVersion}", revisionType: 'TAG']
        }
    }
    timeout(10)
    {
        withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin"])
        {
            stage("Close release")
            {
                sh script: "mvn --batch-mode -U -e nexus-staging:rc-close -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
            }
        }
    }
}
