definition.script:
// Command-line options (taken from https://jenkins.io/doc/pipeline/examples/):
// --batch-mode : recommended in CI to inform maven to not run in interactive mode (less logs)
// -V : strongly recommended in CI, will display the JDK and Maven versions in use.
//      Very useful to be quickly sure the selected versions were the ones you think.
// -U : force maven to update snapshots each time (default : once an hour, makes no sense in CI).
// -Dsurefire.useFile=false : useful in CI. Displays test errors in the logs directly (instead of
//                            having to crawl the workspace files to see the cause).
// -e : Display stack-traces on failure
// -------------------------------------------------------------------------------------------------
//
//
// GCC versions
// ------------
//
// GCC outputs dynamically-linked libraries that depend on specific versions of the glibc library.
// https://gcc.gnu.org/onlinedocs/libstdc++/manual/abi.html lists which GLIBCXX version ships with each version of GCC.
// As of 2017-03-09, AWS linux images ship with GCC 6.4: https://aws.amazon.com/amazon-linux-ami/2017.09-release-notes/
// Therefore, we must use this version of GCC or older.
//
// Using a newer version of GCC results in the following kind of error:
//
// java.lang.UnsatisfiedLinkError: librequirements.so: /usr/lib64/libstdc++.so.6: version `GLIBCXX_3.4.21' not found (required by librequirements.so)
//
// See http://stackoverflow.com/a/17275650/14731 for overriding the GCC version used by CMake
//
// nexus-staging-maven-plugin does not allow us to run unit tests and deploy at the same time because skipNexusStagingDeployMojo cannot be set to true
// in the last module. Therefore we build twice. The first time we run unit tests without deploying. The second time we deploy without running unit tests.
// -Dmaven.javadoc.skip=true is a workaround for https://bugs.openjdk.java.net/browse/JDK-8216170


// Create release candidate branch
def unauthenticatedRepository = "bitbucket.org/cowwoc/requirements/"
def repository = "https://${unauthenticatedRepository}"
// One can look up a project's profileId by running: mvn nexus-staging:rc-list-profiles -DserverId=sonatype-nexus-staging -DnexusUrl=https://oss.sonatype.org/
def stagingProfileId = "353562598afa"
def stagingRepositoryId
def branch = "default"

node("linux")
{
    // http://stackoverflow.com/a/33630506/14731
    withCredentials([[$class: "UsernamePasswordMultiBinding", credentialsId: "bitbucket", usernameVariable: "HG_USERNAME", passwordVariable: "HG_PASSWORD"]])
    {
        def authenticatedRepository = "https://${HG_USERNAME}:${HG_PASSWORD}@${unauthenticatedRepository}"
        timeout(10)
        {
            stage("Checkout (linux)")
            {
                // Wipe out local repository in case it contains extra commits from a previous (broken) build
                deleteDir()
                checkout changelog: true, poll: true, scm: [$class: "MercurialSCM", browser: [$class: "BitBucket", url: repository], clean: true, credentialsId: "bitbucket",  installation: "mercurial", source: repository, revision: "${branch}"]
            }
        }
        timeout(10)
        {
            withEnv(["JAVA_HOME=${tool 'JDK11_HOME'}", "HG_HOME=${ tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin",
                "HGUSER=Gili Tzabari <gili.tzabari@gmail.com>"])
            {
                stage("Create release branch")
                {
                    // Check if tag exists: http://stackoverflow.com/a/41123272/14731
                    def tagExists = !sh(returnStatus: true, script: "hg id -q -r release-${releaseVersion} > /dev/null 2>&1").asBoolean()
                    if (tagExists)
                        echo "Tag release-" + releaseVersion + " already exists"
                    else
                    {
                        // Set commit username/email: http://stackoverflow.com/a/1401821/14731
                        // Commit without failure if nothing changed: http://stackoverflow.com/a/36117152/14731
                        // sh: check if string contains a value: http://stackoverflow.com/a/8811800/14731
                        echo "Tagging release as release-" + releaseVersion
                        sh script: "mvn --batch-mode -V -e versions:set -DnewVersion=${releaseVersion} -DgenerateBackupPoms=false"
                        def hgSummary = sh(returnStdout: true, script: "hg summary")
                        if (!hgSummary.contains("commit: (clean)"))
                            sh script: "hg commit -m \"[jenkins] Releasing version ${releaseVersion}\""

                        sh script: """hg tag release-${releaseVersion}
                            hg update ${branch}
                            mvn --batch-mode -V -e versions:set -DnewVersion=${nextVersion} -DgenerateBackupPoms=false
                            hg commit -m "[jenkins] Incrementing version to ${nextVersion}"
                            hg push ${authenticatedRepository}"""
                    }

                    // Open a new staging repository and capture its ID
                    def output = sh returnStdout: true, script: """hg update release-${releaseVersion}
                        mvn --batch-mode -U -e nexus-staging:rc-open -DstagingProfileId=${stagingProfileId}"""
                    echo output
                    stagingRepositoryId = ( output =~ /Opened (orgbitbucketcowwoc-\d+)/ ) [0][1]
                }
            }
        }
    }
}

// Build and release from all platforms
try
{
    parallel linux:
    {
        // Linux deploys the portable modules as well as its platform-specific binaries.
        // Remaining platforms only deploy platform-specific binaries.
        node("linux")
        {
            timeout(2)
            {
                stage("Checkout")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: "MercurialSCM", browser: [$class: "BitBucket", url: repository], clean: true, credentialsId: "bitbucket",  installation: "mercurial", source: repository, revision: "release-${releaseVersion}", revisionType: "TAG"]
                }
            }
            timeout(15)
            {
                withEnv(["JAVA_HOME=${tool 'JDK11_HOME'}", "PATH+MAVEN=${tool 'Maven'}/bin:${env.JAVA_HOME}/bin"])
                {
                    stage("Deploy (linux)")
                    {
                        // Deploy platform-specific JAR
                        // Use the same GCC version as AWS. See top of this script for more information.
                        sh script: """sudo apt-get install cmake gcc-6 g++-6 -y
                            export CC=/usr/bin/gcc-6
                            export CXX=/usr/bin/g++-6
                            mvn --batch-mode -e -V -U -Dsurefire.useFile=false -DosDetection=false -Plinux-x86_64-release -Dmaven.javadoc.skip=true clean install
                            mvn --batch-mode -e -V -U -Dsurefire.useFile=false -DosDetection=false -Plinux-x86_64-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts "**/target/*.jar, natives/target/linux-x86_64-release/classes/librequirements.so"
                    }
                }
            }
        }
    }, mac:
    {
        node("mac")
        {
            timeout(10)
            {
                stage("Checkout (mac)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: "MercurialSCM", browser: [$class: "BitBucket", url: repository], clean: true, credentialsId: "bitbucket",  installation: "mercurial", source: repository, revision: "release-${releaseVersion}", revisionType: "TAG"]
                }
            }
            timeout(15)
            {
                // gpg resides in /usr/local/bin
                withEnv(["JAVA_HOME=${tool 'JDK11_HOME'}", "PATH+MAVEN=${tool 'Maven'}/bin:${env.JAVA_HOME}/bin:/usr/local/bin"])
                {
                    stage("Deploy (mac)")
                    {
                        sh script: """mvn --batch-mode -e -U -Dsurefire.useFile=false -DosDetection=false -Pmac-x86_64-release -Dmaven.javadoc.skip=true clean install
                            mvn --batch-mode -e -U -Dsurefire.useFile=false -DosDetection=false -Pmac-x86_64-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts "**/target/*.jar, natives/target/mac-x86_64-release/classes/librequirements.dylib"
                    }
                }
            }
        }
    }, windows:
    {
        node("windows")
        {
            timeout(10)
            {
                stage("Checkout (windows)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: "MercurialSCM", browser: [$class: "BitBucket", url: repository], clean: true, credentialsId: "bitbucket",  installation: "mercurial", source: repository, revision: "release-${releaseVersion}", revisionType: "TAG"]
                }
            }
            timeout(15)
            {
                withEnv(["JAVA_HOME=${tool 'JDK11_HOME'}", "HG_HOME=${tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin;${tool 'mercurial'};${env.JAVA_HOME}/bin"])
                {
                    stage("Deploy (windows)")
                    {
                        bat script: """call mvn --batch-mode -e -U -Dsurefire.useFile=false -DosDetection=false -Pwindows-x86_64-release -Dmaven.javadoc.skip=true clean install
                            call mvn --batch-mode -e -U -Dsurefire.useFile=false -DosDetection=false -Pwindows-x86_64-release -Ddeploy deploy -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"""
                        archiveArtifacts "**/target/*.jar, natives/target/windows-x86_64-release/classes/*.dll, natives/target/windows-x86_64-release/classes/*.pdb"
                    }
                }
            }
        }
    },
    failFast: true
}
catch (e)
{
    echo "Dropping staging repository due to error"
    try
    {
        // Drop staging repository
        node("linux")
        {
            timeout(2)
            {
                stage("Checkout (linux)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout changelog: true, poll: true, scm: [$class: "MercurialSCM", browser: [$class: "BitBucket", url: repository], clean: true, credentialsId: "bitbucket",  installation: "mercurial", source: repository, revision: "release-${releaseVersion}", revisionType: "TAG"]
                }
            }
            timeout(5)
            {
                withEnv(["JAVA_HOME=${tool 'JDK11_HOME'}", "PATH+MAVEN=${tool 'Maven'}/bin:${env.JAVA_HOME}/bin"])
                {
                    sh script: "mvn --batch-mode -U -e nexus-staging:rc-drop -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
                }
            }
        }
    }
    catch (e2)
    {
        // Ignore and throw the original exception
    }
    throw e;
}

// Close the release
node("linux")
{
    timeout(2)
    {
        stage("Checkout (linux)")
        {
            // Wipe out local repository in case it contains extra commits from a previous (broken) build
            deleteDir()
            checkout changelog: true, poll: true, scm: [$class: "MercurialSCM", browser: [$class: "BitBucket", url: repository], clean: true, credentialsId: "bitbucket",  installation: "mercurial", source: repository, revision: "release-${releaseVersion}", revisionType: "TAG"]
        }
    }
    timeout(10)
    {
        withEnv(["JAVA_HOME=${tool 'JDK11_HOME'}", "HG_HOME=${tool 'mercurial' }", "PATH+MAVEN+HG=${tool 'Maven'}/bin:${tool 'mercurial'}:${env.JAVA_HOME}/bin"])
        {
            stage("Close release")
            {
                sh script: "mvn --batch-mode -U -e nexus-staging:rc-close -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId}"
            }
        }
    }
}
