definition.script:
// Command-line options (taken from https://jenkins.io/doc/pipeline/examples/):
// --batch-mode : recommended in CI to inform maven to not run in interactive mode (less logs)
// -V : strongly recommended in CI, will display the JDK and Maven versions in use.
//      Very useful to be quickly sure the selected versions were the ones you think.
// -U : force maven to update snapshots each time (default : once an hour, makes no sense in CI).
// -Dsurefire.useFile=false : useful in CI. Displays test errors in the logs directly (instead of
//                            having to crawl the workspace files to see the cause).
// -e : Display stack-traces on failure
// -------------------------------------------------------------------------------------------------
//
//
// GCC versions
// ------------
//
// GCC outputs dynamically-linked libraries that depend on specific versions of the glibc library.
// https://gcc.gnu.org/onlinedocs/libstdc++/manual/abi.html lists which GLIBCXX version ships with each version of GCC.
// As of 2017-03-09, AWS linux images ship with GCC 6.4: https://aws.amazon.com/amazon-linux-ami/2017.09-release-notes/
// Therefore, we must use this version of GCC or older.
//
// Using a newer version of GCC results in the following kind of error:
//
// java.lang.UnsatisfiedLinkError: librequirements.so: /usr/lib64/libstdc++.so.6: version `GLIBCXX_3.4.21' not found (required by librequirements.so)
//
// See http://stackoverflow.com/a/17275650/14731 for overriding the GCC version used by CMake
//
// nexus-staging-maven-plugin does not allow us to run unit tests and deploy at the same time because skipNexusStagingDeployMojo cannot be set to true
// in the last module. Therefore we build twice. The first time we run unit tests without deploying. The second time we deploy without running unit tests.

// Create release candidate branch
def unauthenticatedRepository = "github.com/cowwoc/requirements.java.git"
def repository = "https://${unauthenticatedRepository}"
// One can look up a project's profileId by running: mvn nexus-staging:rc-list-profiles -DserverId=sonatype-nexus-staging -DnexusUrl=https://oss.sonatype.org/
def stagingProfileId = "3799bb102c7f24"
def stagingRepositoryId
def userEmail = "cowwoc2020@gmail.com"
def userName = "Gili Tzabari"
def repositoryIdPrefix = "comgithubcowwoc"

node("linux")
{
    // http://stackoverflow.com/a/33630506/14731
    timeout(10)
    {
        stage("Checkout (linux)")
        {
            // Wipe out local repository in case it contains extra commits from a previous (broken) build
            deleteDir()
            checkout([$class: "GitSCM", branches: [[name: "*/${branch}"]], browser: [$class: "GithubWeb", repoUrl: repository], doGenerateSubmoduleConfigurations: false, extensions: [[$class: "LocalBranch", localBranch: "${branch}"], [$class: "CleanCheckout"], [$class: "WipeWorkspace"], [$class: "MessageExclusion", excludedMessage: "^\\[jenkins\\]"]], submoduleCfg: [], userRemoteConfigs: [[credentialsId: "jenkins", url: repository]]])
        }
    }
    timeout(10)
    {
        withEnv(["JAVA_HOME=${tool "JAVA_HOME"}", "GIT_HOME=${tool "git"}", "PATH+MAVEN+GIT=${tool "Maven"}/bin:${tool "git"}:${env.JAVA_HOME}/bin"])
        {
            stage("Tag release")
            {
                // Check if tag already exists: http://stackoverflow.com/a/21116365/14731
                // Commit without failure if nothing changed: http://stackoverflow.com/a/8123841/14731
                def tagExists = sh(returnStdout: true, script: "git ls-remote ${repository} release-${releaseVersion} | wc -l").toBoolean()
                if (tagExists)
                    echo "Tag release-" + releaseVersion + " already exists"
                else
                {
                    echo "Tagging release as release-" + releaseVersion
                    // SSH authentication: https://www.jenkins.io/blog/2020/04/16/github-app-authentication/
                    withCredentials([usernamePassword(credentialsId: "jenkins", usernameVariable: "GITHUB_APP", passwordVariable: "GITHUB_ACCESS_TOKEN")])
                    {
                        def authenticatedRepository = 'https://${GITHUB_APP}:${GITHUB_ACCESS_TOKEN}@' + unauthenticatedRepository
                        sh script: """git config user.email ${userEmail}
                            git config user.name ${userName}
                            mvn --batch-mode -V -e versions:set -DnewVersion=${releaseVersion} -DgenerateBackupPoms=false
                            git add -u
                            git commit -m "[jenkins] Releasing version ${releaseVersion}"
                            git tag release-${releaseVersion}
                            git checkout ${branch}
                            mvn --batch-mode -e versions:set -DnewVersion=${nextVersion} -DgenerateBackupPoms=false
                            git add -u
                            git diff-index --quiet HEAD || git commit -m "[jenkins] Incrementing version to ${nextVersion}"
                            git push ${authenticatedRepository} ${branch}
                            git push ${authenticatedRepository} release-${releaseVersion}"""
                    }
                }

                // Open a new staging repository and capture its ID
                def output = sh returnStdout: true, script: """git checkout tags/release-${releaseVersion}
                    mvn --batch-mode -e -DstagingProfileId=${stagingProfileId} nexus-staging:rc-open"""
                echo output
                stagingRepositoryId = ( output =~ /Opened (${repositoryIdPrefix}-\d+)/ ) [0][1]
            }
        }
    }
}

// Build and release from all platforms
try
{
    parallel linux:
    {
        // Linux deploys the portable modules as well as its platform-specific binaries.
        // Remaining platforms only deploy platform-specific binaries.
        node("linux")
        {
            timeout(10)
            {
                stage("Checkout")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout([$class: "GitSCM", branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: "GithubWeb", repoUrl: repository], doGenerateSubmoduleConfigurations: false, extensions: [[$class: "LocalBranch", localBranch: "release-${releaseVersion}"], [$class: "CleanCheckout"], [$class: "WipeWorkspace"], [$class: "MessageExclusion", excludedMessage: "^\\[jenkins\\]"]], submoduleCfg: [], userRemoteConfigs: [[credentialsId: "jenkins", url: repository]]])
                }
            }
            timeout(40)
            {
                // gpg resides in /usr/local/bin
                withEnv(["JAVA_HOME=${ tool "JAVA_HOME" }", "PATH+MAVEN=${tool "Maven"}/bin:${env.JAVA_HOME}/bin:/usr/local/bin"])
                {
                    stage("Deploy (linux)")
                    {
                        // Deploy platform-specific JAR
                        // Use the same GCC version as AWS. See top of this script for more information.
                        sh script: """sudo apt-get install cmake gcc-6 g++-6 -y
                            export CC=/usr/bin/gcc-6
                            export CXX=/usr/bin/g++-6
                            mvn --batch-mode -e -V -U -Dsurefire.useFile=false install
                            mvn --batch-mode -e -V -U -Dsurefire.useFile=false -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId} -Ddeploy deploy"""
                        archiveArtifacts artifacts: "**/target/*.jar, natives/target/linux-x86_64-release/*.jar", excludes: "**/target/test-classes/**"
                    }
                }
            }
        }
    }, mac:
    {
        node("mac")
        {
            timeout(10)
            {
                stage("Checkout (mac)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout([$class: "GitSCM", branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: "GithubWeb", repoUrl: repository], doGenerateSubmoduleConfigurations: false, extensions: [[$class: "LocalBranch", localBranch: "release-${releaseVersion}"], [$class: "CleanCheckout"], [$class: "WipeWorkspace"], [$class: "MessageExclusion", excludedMessage: "^\\[jenkins\\]"]], submoduleCfg: [], userRemoteConfigs: [[credentialsId: "jenkins", url: repository]]])
                }
            }
            timeout(40)
            {
                // gpg resides in /usr/local/bin
                withEnv(["JAVA_HOME=${ tool "JAVA_HOME" }", "PATH+MAVEN=${tool "Maven"}/bin:${env.JAVA_HOME}/bin:/usr/local/bin"])
                {
                    stage("Deploy (mac)")
                    {
                        sh script: """mvn --batch-mode -e -V -U -Dsurefire.useFile=false install
                            mvn --batch-mode -e -V -U -Dsurefire.useFile=false -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId} -Ddeploy deploy"""
                            archiveArtifacts artifacts: "**/target/*.jar, natives/target/mac-x86_64-release/*.jar", excludes: "**/target/test-classes/**"
                    }
                }
            }
        }
    }, windows:
    {
        node("windows")
        {
            timeout(10)
            {
                stage("Checkout (windows)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout([$class: "GitSCM", branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: "GithubWeb", repoUrl: repository], doGenerateSubmoduleConfigurations: false, extensions: [[$class: "LocalBranch", localBranch: "release-${releaseVersion}"], [$class: "CleanCheckout"], [$class: "WipeWorkspace"], [$class: "MessageExclusion", excludedMessage: "^\\[jenkins\\]"]], submoduleCfg: [], userRemoteConfigs: [[credentialsId: "jenkins", url: repository]]])
                }
            }
            timeout(40)
            {
                // Windows needs a longer timeout due to Windows Defender (antivirus) slowing things down
                withEnv(["JAVA_HOME=${ tool "JAVA_HOME" }", "PATH+MAVEN=${tool "Maven"}/bin;${env.JAVA_HOME}/bin"])
                {
                    stage("Deploy (windows)")
                    {
                        bat script: """call mvn --batch-mode -e -V -U -Dsurefire.useFile=false install
                            call mvn --batch-mode -e -V -U -Dsurefire.useFile=false -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId} -Ddeploy deploy"""
                        archiveArtifacts artifacts: "**/target/*.jar, natives/target/windows-x86_64-release/*.jar", excludes: "**/target/test-classes/**"
                    }
                }
            }
        }
    },
    failFast: true
}
catch (e)
{
    echo "Dropping staging repository due to error"
    try
    {
        // Drop staging repository
        node("linux")
        {
            timeout(10)
            {
                stage("Checkout (linux)")
                {
                    // Wipe out local repository in case it contains extra commits from a previous (broken) build
                    deleteDir()
                    checkout([$class: "GitSCM", branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: "GithubWeb", repoUrl: repository], doGenerateSubmoduleConfigurations: false, extensions: [[$class: "LocalBranch", localBranch: "release-${releaseVersion}"], [$class: "CleanCheckout"], [$class: "WipeWorkspace"], [$class: "MessageExclusion", excludedMessage: "^\\[jenkins\\]"]], submoduleCfg: [], userRemoteConfigs: [[credentialsId: "jenkins", url: repository]]])
                }
            }
            timeout(5)
            {
                withEnv(["JAVA_HOME=${ tool "JAVA_HOME" }", "PATH+MAVEN=${tool "Maven"}/bin:${env.JAVA_HOME}/bin"])
                {
                    sh script: "mvn --batch-mode -e -V -U -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId} nexus-staging:rc-drop"
                }
            }
        }
    }
    catch (e2)
    {
        // Ignore and throw the original exception
    }
    throw e;
}

// Close the release
node("linux")
{
    timeout(10)
    {
        stage("Checkout (linux)")
        {
            // Wipe out local repository in case it contains extra commits from a previous (broken) build
            deleteDir()
            checkout([$class: "GitSCM", branches: [[name: "refs/tags/release-${releaseVersion}"]], browser: [$class: "GithubWeb", repoUrl: repository], doGenerateSubmoduleConfigurations: false, extensions: [[$class: "LocalBranch", localBranch: "release-${releaseVersion}"], [$class: "CleanCheckout"], [$class: "WipeWorkspace"], [$class: "MessageExclusion", excludedMessage: "^\\[jenkins\\]"]], submoduleCfg: [], userRemoteConfigs: [[credentialsId: "jenkins", url: repository]]])
        }
    }
    timeout(10)
    {
        withEnv(["JAVA_HOME=${ tool "JAVA_HOME" }", "PATH+MAVEN=${tool "Maven"}/bin:${env.JAVA_HOME}/bin"])
        {
            stage("Close release")
            {
                sh script: "mvn --batch-mode -e -V -U -DstagingProfileId=${stagingProfileId} -DstagingRepositoryId=${stagingRepositoryId} nexus-staging:rc-close"
            }
        }
    }
}
