definition.script:
// GCC versions
// ------------
//
// GCC outputs dynamically-linked libraries that depend on specific versions of the glibc library.
// https://gcc.gnu.org/onlinedocs/libstdc++/manual/abi.html lists which GLIBCXX version ships with each version of GCC.
// As of 2017-03-09, AWS linux images ship with GCC 4.8.3: https://aws.amazon.com/amazon-linux-ami/2015.09-release-notes/
// Therefore, we must use this version of GCC or older.
//
// Using a newer version of GCC results in the following error:
//
// java.lang.UnsatisfiedLinkError: librequirements.so: /usr/lib64/libstdc++.so.6: version `GLIBCXX_3.4.21' not found (required by librequirements.so)
//
// See http://stackoverflow.com/a/17275650/14731 for overriding the GCC version used by CMake

parallel windows:
{
    node("windows && i386 && amd64")
    {
        timeout(10)
        {
            stage("Checkout (windows)")
            {
                // Wipe out local repository in case it contains extra commits from a previous (broken) build
                deleteDir()
                checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket',  installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "${branch}"]
            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin;${env.JAVA_HOME}/bin"])
            {
                stage("Deploy (windows-x86_32)")
                {
                    // https://jenkins.io/doc/pipeline/examples/
                    // Apache Maven related side notes:
                    // --batch-mode : recommended in CI to inform maven to not run in interactive mode (less logs)
                    // -V : strongly recommended in CI, will display the JDK and Maven versions in use.
                    //      Very useful to be quickly sure the selected versions were the ones you think.
                    // -U : force maven to update snapshots each time (default : once an hour, makes no sense in CI).
                    // -Dsurefire.useFile=false : useful in CI. Displays test errors in the logs directly (instead of
                    //                            having to crawl the workspace files to see the cause).            
                    bat script: 'mvn --batch-mode -V -U -e clean deploy -Pwindows-x86_32-release -Dsurefire.useFile=false'
                    archiveArtifacts '**/target/*.jar'
                }
            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin;${env.JAVA_HOME}/bin"])
            {
                stage("Deploy (windows-amd64)")
                {
                    bat script: 'mvn --batch-mode -V -U -e clean deploy -Pwindows-x86_64-release -Dsurefire.useFile=false'
                    archiveArtifacts '**/target/*.jar'
                }
            }
        }
    }
}, linux_i386:
{
    node("linux && i386")
    {
        timeout(10)
        {
            stage("Checkout (linux-i386)")
            {
                // Wipe out local repository in case it contains extra commits from a previous (broken) build
                deleteDir()
                checkout changelog: true, poll: true, scm: [$class: 'MercurialSCM', browser: [$class: 'BitBucket', url: 'https://bitbucket.org/cowwoc/requirements/'], clean: true, credentialsId: 'bitbucket', installation: 'mercurial', source: 'https://bitbucket.org/cowwoc/requirements/', revision: "${branch}"]
            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin:${env.JAVA_HOME}/bin"])
            {
                stage("Deploy (linux-i386)")
                {
                    // Use the same GCC version as AWS. See top of this script for more information.
                    sh script: '''sudo apt-get install cmake gcc-4.7 g++-4.7 -y
                    export CC=/usr/bin/gcc-4.7
                    export CXX=/usr/bin/g++-4.7
                    mvn --batch-mode -V -U -e clean deploy -Plinux-x86_32-release -Dsurefire.useFile=false'''
                    archiveArtifacts '**/target/*.jar'
                }
            }
        }
    }
